package J0701;
/**
 * 標準輸出格式練習
 * %s為文字
 * %n為換行
 * */
public class Test2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.printf("%s%s%5.4f %s%n","Pi","=",3.1415,"圓周率");
		System.out.printf("%s %s %2.1f%s%s %s%n","Java","ver",1.8,"_","06","版");
		System.out.printf("%s %s %s%n","YVTS","100","椰");
		System.out.printf("%s %s %s%n","I","love","\"Java\"");
		System.out.printf("%s %s %s","I","love","\\Java\\");
	}
}